Graph.CDATA:
using System;
using PX.Data;

namespace SustainabilityShipping
{
  public class SSHSustainabilityMaint : PXGraph<SSHSustainabilityMaint>
  {

    public PXSave<SSHLevelSustainability> Save;
    public PXCancel<SSHLevelSustainability> Cancel;

    public PXSelect<SSHLevelSustainability> DetailsView;

   public override void Persist(){
    if(DetailsView.Current != null){
          SSHLevelSustainability sustainability = new SSHLevelSustainability { SustainabilityID = DetailsView.Current.SustainabilityID, LevelName = DetailsView.Current.LevelName, LevelDescription = DetailsView.Current.LevelDescription };
          DetailsView.Insert(sustainability);
          base.Persist();
        }
    }
  }
}
